import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import styled from "styled-components";
import SubTop from "Components/SubTop";
import SubTitle from "Components/EduReviewComponent/SubTitle";
import Slide from "Components/EduReviewComponent/Slide";
import Board from "Components/EduReviewComponent/Board";
import Form from "Routes/EduReviewForm";
import ReviewDetail from "Routes/EduReviewDetail";

const Box = styled.div`
    width: 70vw;
    margin: 0 auto;
`;

const ButtonArea = styled.nav`
    width: 100%;
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    gap: 20px;
    button {
        height: 50px;
        border: 0.5px solid rgba(0, 0, 0, 0.1);
        font-size: 15px;
        color: ${(props) => props.focuscheck && "white"};
        background: ${(props) => props.focuscheck && "var(--main-color)"};
    }
`;

const EduReivewPresenter = ({
    subTopData,
    SlideData,
    boardData,
    setButtonSelect,
    buttonSelect,
}) => {
    const typeHandler = (index) => {
        let arr = [0, 0, 0, 0, 0];
        arr[index] = 1;
        setButtonSelect(arr);
        console.log(buttonSelect);
    };

    return (
        <>
            <SubTop subTopData={subTopData} />
            <Box>
                <SubTitle title="베스트 교육 후기" />
                <Slide SlideData={SlideData} />
                <ButtonArea>
                    <button focuscheck={1} onClick={() => typeHandler(0)}>
                        전체
                    </button>
                    <button
                        focuscheck={buttonSelect[1]}
                        onClick={() => typeHandler(1)}
                    >
                        청소년 캠프
                    </button>
                    <button
                        focuscheck={buttonSelect[2]}
                        onClick={() => typeHandler(2)}
                    >
                        찾아가는 체험교실
                    </button>
                    <button
                        focuscheck={buttonSelect[3]}
                        onClick={() => typeHandler(3)}
                    >
                        청소년 동아리
                    </button>
                    <button
                        focuscheck={buttonSelect[4]}
                        onClick={() => typeHandler(4)}
                    >
                        전문인 특강 / 창의체험부스
                    </button>
                </ButtonArea>
                <Route
                    path="/review"
                    exact
                    render={() => <Board boardData={boardData} />}
                />
                <Switch>
                    <Route path="/review/form" component={Form} />
                    <Route path="/review/:id" component={ReviewDetail} />
                </Switch>
            </Box>
        </>
    );
};

export default EduReivewPresenter;
