import React from "react";
import styled from "styled-components";
import EditorComponent from "Components/EduReviewForm/EditorComponent";

const Box = styled.div`
    width: 70vw;
    margin: 0 auto;
`;

styled.form`
    display: flex;
    flex-direction: column;
`;

const Title = styled.h1`
    font-size: 30px;
    font-weight: 500;
`;

const Input = styled.input`
    height: 40px;
    padding-left: 20px;
    width: 90%;
`;
const Select = styled.select`
    height: 40px;
    padding-left: 20px;
    width: 60%;
`;

const Label = styled.label`
    width: 10%;
    padding-right: 2%;
    display: flex;
    align-items: center;
    justify-content: end;
`;
const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    margin-bottom: 40px;
`;
const Button = styled.button`
    width: 80px;
    height: 50px;
    font-size: 18px;
    border-radius: 7px;
    background: white;
    border: 0.5px solid black;
    :hover {
        background: rgba(0, 0, 0, 0.1);
    }
`;

const CheckBox = styled.input`
    width: 20px;
    height: 40px;
    display: flex;
    align-items: center;
`;

const InputContainer = styled.div`
    display: flex;
    width: 100%;
    margin: 10px 0;
    & > input,
    select {
        border: 1px solid rgba(0, 0, 0, 0.3);
        border-radius: 5px;
    }
`;

const FAQPresenter = ({
    onChangeTitle,
    onChangeMain,
    onChangeCategory,
    onChangeSecret,
    submitHandler,
    title,
    main,
    category,
    secret,
}) => {
    const [postBlockArr, setPostBlockArr] = useState([]);
    const user = JSON.parse(sessionStorage.getItem("webToken"));

    useEffect(() => {
        async function fetchData() {
            let {
                data: { faqs },
            } = await faqApi.getList();
            setPostBlockArr(faqs);
        }
        fetchData();
    }, []);

    const PostBlock = () => {
        return postBlockArr !== []
            ? postBlockArr.map((item, index) => (
                  <div
                      className="faq-post-block"
                      key={index}
                      onClick={handleClick}
                  >
                      <div className="faq-post-block-column">
                          <div className="faq-post-text">
                              {item.title}
                              <span>{item.var ? " 🔼" : " 🔽"}</span>
                          </div>
                      </div>
                      <div
                          className="faq-post-hidden-box"
                          dangerouslySetInnerHTML={{ __html: item.content }}
                      />
                  </div>
              ))
            : null;
    };

    const handleClick = (e) => {
        e.currentTarget.classList.toggle("faq-active");
    };
    return (
        <div className="faq-wrap">
            <div className="faq-board">
                <div className="faq-path">
                    홈 &nbsp;&gt;&nbsp; 고객센터 &nbsp; &gt;&nbsp; 1:1 문의
                </div>
                <div className="faq-post-wrap">
                    <div className="faq-text">
                        <p> 자주 묻는 질문 </p>
                        <p> 포트레이트에 궁금한 점이 있으신가요? </p>
                        <p> 자주 묻는 질문 FAQ 리스트를 확인해보세요. </p>
                    </div>
                    <PostBlock />
                </div>
                {user ? (
                    <button className="faq-register-btn">
                        <Link to={"/faq/post"}>자주 묻는 질문 등록</Link>
                    </button>
                ) : null}
            </div>
            <div className="faq-help-wrap">
                <div className="faq-help-text">
                    <span>원하는 답변을 찾기 어려우신가요?</span>
                    <span>링커벨에게 직접 문의 해주세요</span>
                    <span>최대한 빠르게 도와드리겠습니다!</span>
                    <Link to="/inquiry" className="faq-help-btn">
                        <p>1:1 문의 하기</p>
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default FAQPresenter;
